settings.code_cus_css:
.t4s-featured-products
{
background-color: #fff;
}
.t4s-section-title > span {
  font-weight: 800;
}
.t4s-section-title>span:has(i)
{
  display:none!important;
}
@media only screen and (max-width: 767px) {
 .t4s-section-title {
 text-align: left;
  }
.t4s-section-title>span:has(i)
{
  display:inline-block!important;
}
.t4s-top-heading
{
margin-bottom: 5px!important;
}
.t4s-section-title > span
{
    font-size: 16px!important;
    text-transform: capitalize!important;
 font-weight: 500;
}
.t4s-product .t4s-product-title a
{
font-size: 12px!important;
}
.t4s-row-cols-3>.t4s-col-item, .t4s-row-cols-3 .flickityt4s-slider>.t4s-col-item {
    width: 30.333333%;
}
}

a.t4s-collection-title {
    font-size: 18px;
    font-weight: 600;
    margin-top: 10px;
    display: inline-block;
}
.t4s-section-title > span {
  font-style: normal;
  text-transform: uppercase;
}
@media only screen and (max-width: 767px) {
a.t4s-collection-title
{
   font-size: 12px;
}
.swiper-container:before,
.swiper-container:after
{
  display: none;
}
}

.t4s-collection-title{
font-family: var(--font-heading-family)!important;
text-transform: uppercase;
font-weight: 800!important;
font-size: 12px!important;
}
@media only screen and (max-width: 1025px) {
.t4s-section-title>span
{
  font-size: 18px!important;
}
}

.t4s-product-wrapper {
    text-align: center;
}
.t4s-product .t4s-product-info  .t4s-product-title
{
margin-bottom: 5px!important;
}
.t4s-product .t4s-product-title a,.t4s-product .t4s-product-price{
    font-weight: 600!important;
font-size: 15px;
letter-spacing: 0.5px;
}
.t4s-tabs-border-bg.t4s-item-rounded-true .t4s-tabs-ul li a
{
border: 1px solid #ddd;
}
.t4s-tabs-border-bg.t4s-item-rounded-true .t4s-tabs-ul li a.t4s-active
{
border: 1px solid #222;
}

.t4s-tabs-border-bg.t4s-item-rounded-true .t4s-tabs-ul li a:hover,.t4s-tabs-border-bg.t4s-item-rounded-true .t4s-tabs-ul li a.t4s-active:hover
{
border: 1px solid #ddd;
-webkit-transform: scale(1.02);
    transform: scale(1.02);
    box-shadow: 0 4px 8px 0 rgba(0,0,0,.1);
}
.t4s-tabs-border-bg.t4s-item-rounded-true .t4s-tabs-ul li a.t4s-active:hover
{
border: 1px solid #222;
}
.t4s-tabs-ul .flickityt4s-viewport
{
      height: 42px !important;
}
